CREATE TABLE dealRecord (	fDate TEXT,	fTime TEXT,	fPay REAL,	fReceive REAL,	fBalance REAL,	fDeal TEXT,	fDealDetail TEXT,	fClass TEXT,	fClassDetail TEXT);"fDate","fTime","fPay","fReceive","fBalance","fDeal","fDealDetail","fClass","fClassDetail"#最近12个月，每月P2P投资额SELECT strftime("%Y-%m", fdate) AS date, sum(fpay)FROM dealRecordWHERE fclass in ("P2P投资", "P2P还款")GROUP BY strftime("%Y-%m", fdate)ORDER BY date DESCLIMIT 12#2019年前10月均P2P投资额SELECT sum(fpay)/10FROM dealRecordWHERE fclass in ("P2P投资", "P2P还款")AND strftime("%Y-%m", fdate) > strftime("%Y-%m", "2018-12-31")